import './App.css';

function ProductDetail({ match }) {

    useEffect( () => {
        fetchProduct();
    }, []);

    const [product, setProduct] = useState({});

    const fetchProduct = async () => {
        const fetchProduct = await fetch(`http://127.0.0.1:3000/shop/${match.params.id}`);
        console.log(fetchProduct)
        
        setProduct(product);

    }

    return (
    <div>
        <h1>{product.name}</h1>
    </div>
  );
}

export default ProductDetail;
